import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../../hooks/useAuth';
import { userService } from '../../services/userService';
import { chatService } from '../../services/chatService';
import { CalendarIcon, ChatBubbleLeftRightIcon, DocumentTextIcon, ArrowPathIcon } from '@heroicons/react/24/outline';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import { Line } from 'react-chartjs-2';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const Dashboard = () => {
  const { user } = useAuth();
  const [conversations, setConversations] = useState([]);
  const [upcomingSession, setUpcomingSession] = useState(null);
  const [sentimentData, setSentimentData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({
    totalSessions: 0,
    averageSentiment: 0,
    improvementTrend: 'stable',
    lastSessionDate: null
  });
  const [resources, setResources] = useState([]);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true);

        // Fetch recent conversations
        const recentConversations = await userService.getRecentConversations(user.id, 'client', 5);
        setConversations(recentConversations || []);

        // Fetch upcoming session
        const upcomingSessions = await userService.getUpcomingSessions(user.id, 'client');
        setUpcomingSession(upcomingSessions?.[0] || null);

        // Fetch sentiment data for chart if there are conversations
        if (recentConversations && recentConversations.length > 0) {
          const mostRecentConversationId = recentConversations[0].id;

          // In a real implementation, this would be a proper API call
          // For now, we'll simulate with mock data
          const messages = await chatService.getConversationHistory(mostRecentConversationId);

          // Process sentiment data for chart
          if (messages && messages.length > 0) {
            const sentimentScores = messages
              .filter(m => m.sentiment_score !== null && m.sentiment_score !== undefined)
              .map(m => ({
                timestamp: m.timestamp,
                score: m.sentiment_score
              }));

            if (sentimentScores.length > 0) {
              // Group by date for the chart
              const dateGroups = {};
              sentimentScores.forEach(item => {
                const date = new Date(item.timestamp).toLocaleDateString();
                if (!dateGroups[date]) {
                  dateGroups[date] = [];
                }
                dateGroups[date].push(item.score);
              });

              // Calculate average for each date
              const labels = Object.keys(dateGroups).sort((a, b) => new Date(a) - new Date(b));
              const data = labels.map(date => {
                const scores = dateGroups[date];
                return scores.reduce((sum, score) => sum + score, 0) / scores.length;
              });

              const chartData = {
                labels,
                datasets: [
                  {
                    label: 'Emotional Well-being',
                    data,
                    borderColor: 'rgb(56, 189, 248)',
                    backgroundColor: 'rgba(56, 189, 248, 0.1)',
                    fill: true,
                    tension: 0.4
                  }
                ]
              };
              setSentimentData(chartData);

              // Calculate stats
              const allScores = sentimentScores.map(item => item.score);
              const averageSentiment = allScores.reduce((sum, score) => sum + score, 0) / allScores.length;

              // Determine trend (simple version - compare first half to second half)
              const midpoint = Math.floor(allScores.length / 2);
              const firstHalf = allScores.slice(0, midpoint);
              const secondHalf = allScores.slice(midpoint);

              const firstHalfAvg = firstHalf.reduce((sum, score) => sum + score, 0) / firstHalf.length;
              const secondHalfAvg = secondHalf.reduce((sum, score) => sum + score, 0) / secondHalf.length;

              let trend = 'stable';
              if (secondHalfAvg > firstHalfAvg + 0.1) {
                trend = 'improving';
              } else if (secondHalfAvg < firstHalfAvg - 0.1) {
                trend = 'declining';
              }

              setStats({
                totalSessions: recentConversations.length,
                averageSentiment: averageSentiment.toFixed(2),
                improvementTrend: trend,
                lastSessionDate: new Date(recentConversations[0].start_ts).toLocaleDateString()
              });
            }
          }
        }

        // Mock resources data
        setResources([
          {
            id: 1,
            title: 'Breathing Exercises',
            description: 'Simple techniques to manage anxiety and stress',
            type: 'Exercise',
            url: '#'
          },
          {
            id: 2,
            title: 'Sleep Improvement Guide',
            description: 'Strategies for better sleep quality',
            type: 'Article',
            url: '#'
          },
          {
            id: 3,
            title: 'Mindfulness Meditation',
            description: '10-minute guided meditation for beginners',
            type: 'Audio',
            url: '#'
          }
        ]);
      } catch (error) {
        console.error('Error fetching dashboard data:', error);
      } finally {
        setLoading(false);
      }
    };

    if (user && user.id) {
      fetchDashboardData();
    }
  }, [user]);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-full">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-500"></div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
      {/* Welcome Section */}
      <div className="bg-gradient-to-r from-primary-500 to-primary-600 rounded-xl shadow-lg p-6 mb-8 text-white">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold">
              Welcome back, {user.profile_data?.name || 'Client'}
            </h1>
            <p className="mt-2 text-primary-100 max-w-2xl">
              Track your progress, manage your therapy sessions, and access resources to support your mental health journey.
            </p>
          </div>
          <div className="hidden md:block">
            <img 
              src="https://cdn-icons-png.flaticon.com/512/3209/3209993.png" 
              alt="Wellness" 
              className="w-24 h-24 opacity-80"
            />
          </div>
        </div>
      </div>

      {/* Stats Overview */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
        <div className="bg-white rounded-xl shadow p-6 flex flex-col items-center justify-center text-center">
          <span className="text-3xl font-bold text-primary-500">{stats.totalSessions}</span>
          <span className="text-sm text-gray-500 mt-2">Total Sessions</span>
        </div>

        <div className="bg-white rounded-xl shadow p-6 flex flex-col items-center justify-center text-center">
          <span className="text-3xl font-bold text-primary-500">{stats.averageSentiment}</span>
          <span className="text-sm text-gray-500 mt-2">Average Well-being Score</span>
        </div>

        <div className="bg-white rounded-xl shadow p-6 flex flex-col items-center justify-center text-center">
          <span className={`text-3xl font-bold ${stats.improvementTrend === 'improving' ? 'text-green-500' : stats.improvementTrend === 'declining' ? 'text-red-500' : 'text-yellow-500'}`}>
            {stats.improvementTrend === 'improving' ? '↑' : stats.improvementTrend === 'declining' ? '↓' : '→'}
          </span>
          <span className="text-sm text-gray-500 mt-2">Progress Trend</span>
        </div>

        <div className="bg-white rounded-xl shadow p-6 flex flex-col items-center justify-center text-center">
          <span className="text-3xl font-bold text-primary-500">{stats.lastSessionDate || 'None'}</span>
          <span className="text-sm text-gray-500 mt-2">Last Session</span>
        </div>
      </div>

      {/* Main Content Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Left Column */}
        <div className="lg:col-span-2 space-y-8">
          {/* Quick Actions */}
          <div className="bg-white rounded-xl shadow p-6">
            <h2 className="text-xl font-semibold text-gray-800 mb-4">Quick Actions</h2>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <Link
                to="/client/chat"
                className="flex items-center p-4 bg-primary-50 rounded-lg hover:bg-primary-100 transition-colors group"
              >
                <div className="p-3 bg-primary-100 rounded-full group-hover:bg-primary-200 transition-colors">
                  <ChatBubbleLeftRightIcon className="h-6 w-6 text-primary-600" />
                </div>
                <div className="ml-4">
                  <h3 className="font-medium text-gray-900">Chat Session</h3>
                  <p className="text-sm text-gray-500">Talk with your AI therapist</p>
                </div>
              </Link>

              <button
                onClick={() => {/* Schedule session logic */}}
                className="flex items-center p-4 bg-primary-50 rounded-lg hover:bg-primary-100 transition-colors group text-left"
              >
                <div className="p-3 bg-primary-100 rounded-full group-hover:bg-primary-200 transition-colors">
                  <CalendarIcon className="h-6 w-6 text-primary-600" />
                </div>
                <div className="ml-4">
                  <h3 className="font-medium text-gray-900">Schedule</h3>
                  <p className="text-sm text-gray-500">Book a live session</p>
                </div>
              </button>

              <button
                onClick={() => {/* View resources logic */}}
                className="flex items-center p-4 bg-primary-50 rounded-lg hover:bg-primary-100 transition-colors group text-left"
              >
                <div className="p-3 bg-primary-100 rounded-full group-hover:bg-primary-200 transition-colors">
                  <DocumentTextIcon className="h-6 w-6 text-primary-600" />
                </div>
                <div className="ml-4">
                  <h3 className="font-medium text-gray-900">Resources</h3>
                  <p className="text-sm text-gray-500">Access helpful materials</p>
                </div>
              </button>
            </div>
          </div>

          {/* Progress Chart */}
          {sentimentData && (
            <div className="bg-white rounded-xl shadow p-6">
              <div className="flex justify-between items-center mb-6">
                <h2 className="text-xl font-semibold text-gray-800">Your Well-being Progress</h2>
                <button 
                  className="text-primary-500 hover:text-primary-700 flex items-center text-sm font-medium"
                  onClick={() => {/* Refresh chart data */}}
                >
                  <ArrowPathIcon className="h-4 w-4 mr-1" />
                  Refresh
                </button>
              </div>
              <div className="h-64">
                <Line
                  data={sentimentData}
                  options={{
                    responsive: true,
                    maintainAspectRatio: false,
                    plugins: {
                      legend: {
                        position: 'top',
                      },
                      tooltip: {
                        callbacks: {
                          label: function(context) {
                            return `Score: ${context.parsed.y.toFixed(2)}`;
                          }
                        }
                      }
                    },
                    scales: {
                      y: {
                        beginAtZero: true,
                        max: 1,
                        title: {
                          display: true,
                          text: 'Well-being Score'
                        }
                      },
                      x: {
                        title: {
                          display: true,
                          text: 'Date'
                        }
                      }
                    }
                  }}
                />
              </div>
              <p className="mt-4 text-sm text-gray-500 italic">
                This chart shows your emotional well-being trends based on your conversations with the AI therapist.
              </p>
            </div>
          )}

          {/* Recent Conversations */}
          <div className="bg-white rounded-xl shadow p-6">
            <h2 className="text-xl font-semibold text-gray-800 mb-4">Recent Sessions</h2>
            <div className="space-y-4">
              {conversations.map((conversation) => (
            <div
              key={conversation.id}
              className="border-b border-secondary-200 last:border-0 pb-4 last:pb-0"
            >
              <div className="flex justify-between items-start">
                <div>
                  <p className="text-secondary-900 font-medium">
                    {new Date(conversation.start_ts).toLocaleDateString()}
                  </p>
                  <p className="text-secondary-600 text-sm mt-1">
                    {conversation.summaries?.[0]?.summary_text || 'No summary available'}
                  </p>
                </div>
                <button
                  onClick={() => {/* View details logic */}}
                  className="text-primary-600 hover:text-primary-700 text-sm font-medium"
                >
                  View Details
                </button>
              </div>
            </div>
          ))}
            </div>
          </div>
        </div>

        {/* Right Column */}
        <div className="space-y-8">
          {/* Upcoming Session */}
          {upcomingSession ? (
            <div className="bg-white rounded-xl shadow p-6">
              <h2 className="text-xl font-semibold text-gray-800 mb-4">Upcoming Session</h2>
              <div className="bg-primary-50 p-4 rounded-lg">
                <div className="flex items-center mb-4">
                  <div className="h-12 w-12 rounded-full bg-primary-100 flex items-center justify-center mr-4">
                    <CalendarIcon className="h-6 w-6 text-primary-600" />
                  </div>
                  <div>
                    <h3 className="font-medium text-gray-900">
                      With Dr. {upcomingSession.therapists?.name || 'Therapist'}
                    </h3>
                    <p className="text-sm text-gray-500">
                      {new Date(upcomingSession.start_time).toLocaleString()}
                    </p>
                  </div>
                </div>
                <button
                  onClick={() => {/* Join session logic */}}
                  className="w-full bg-primary-500 text-white px-4 py-2 rounded-lg hover:bg-primary-600 transition-colors flex items-center justify-center"
                >
                  Join Session
                </button>
              </div>
            </div>
          ) : (
            <div className="bg-white rounded-xl shadow p-6">
              <h2 className="text-xl font-semibold text-gray-800 mb-4">No Upcoming Sessions</h2>
              <div className="bg-gray-50 p-4 rounded-lg text-center">
                <p className="text-gray-600 mb-4">You don't have any upcoming sessions scheduled.</p>
                <button
                  onClick={() => {/* Schedule session logic */}}
                  className="bg-primary-500 text-white px-4 py-2 rounded-lg hover:bg-primary-600 transition-colors"
                >
                  Schedule Now
                </button>
              </div>
            </div>
          )}

          {/* Resources */}
          <div className="bg-white rounded-xl shadow p-6">
            <h2 className="text-xl font-semibold text-gray-800 mb-4">Helpful Resources</h2>
            <div className="space-y-3">
              {resources.map(resource => (
                <a
                  key={resource.id}
                  href={resource.url}
                  className="block p-4 bg-gray-50 rounded-lg hover:bg-gray-100 transition-colors"
                >
                  <div className="flex justify-between items-center">
                    <div>
                      <h3 className="font-medium text-gray-900">{resource.title}</h3>
                      <p className="text-sm text-gray-600 mt-1">{resource.description}</p>
                    </div>
                    <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-primary-100 text-primary-800">
                      {resource.type}
                    </span>
                  </div>
                </a>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
